function show_edit(t,e){var n="comment-"+t,o=$(".comment-"+t).html();$("."+n).replaceWith('<div class="comment comment-'+t+'">'+o+'<div class="edit_area"><textarea class="form-control" id="new_comment_field_'+t+'"></textarea><br><button class="btn btn-primary update-confirm" onclick="edit_comment('+t+", '"+e+'\')">Update Comment</button><button class="btn btn-default update-cancel" onclick="cancel_edit('+t+')"> Cancel</button></div></div>'),$(".edit-button").hide(),$("form[name=comment-form]").hide()}function cancel_edit(t){$(".edit_area").remove(),$(".edit-button").show(),$("form[name=comment-form]").show()}function new_comment_text(t){return document.getElementById("new_comment_field_"+t).value}function edit_comment(t,e){$.ajax({dataType:"json",url:"/commentajax/",type:"POST",data:JSON.stringify({comment_id:t,csrf_token:e,new_text:new_comment_text(t)})}).done(function(e){var n="comment-content-"+t,o='<div class="comment-body '+n+'">';$("."+n).replaceWith(o+e.new_text+"</div>"),$(".edit_area").remove(),$("form[name=comment-form]").show(),$(".edit-button").show()})}
